class PortfolioProject{
    constructor(e,t,i,r,o,n){
        this.title=e,
        this.urlref=t,
        this.imgref=i,
        this.imghoverref=r,
        this.description=o,
        this.type=n
    }
    
    SerializeAsMain(){return "<a href='"+this.urlref+"' onclick='return clickableProject(this)' target='_blank'>         <div class='pBorder'>             <article style='background-image:url("+this.imgref+")'>                 <div class='description'>                     <h4>"+this.title+"</h4>                     <div><p>"+this.description+"</p></div>                     <p class='team'>"+this.type+"</p>                 </div>                 <div style='background-image:url("+this.imghoverref+")' class='hov'></div>             </article>         </div>     </a>"}
    SerializeAsRelease(){return"<a href="+this.urlref+'onclick="return clickableProject(this)" target="_blank">\n        <div class="pBorder">\n            <article style="background-image:url('+this.imgref+')">\n                <div class="description">\n                    <h4>'+this.title+"</h4>\n                    <div>\n                        <p>"+this.description+'</p>\n                    </div>\n                    <p class="team">'+this.type+'</p>\n                </div>\n                <div style="background-image:url('+this.imghoverref+')" class="hov"></div>\n            </article>\n        </div>\n    </a>'}
}
    
   